import React, { useEffect, useRef } from 'react';
import { View, Animated, Button } from 'react-native';

function App() {



  const fadeAnim = useRef(new Animated.Value(1)).current;


  const toRight = () => {
    Animated.timing(fadeAnim, {
      toValue: 100,
      duration: 500,
      useNativeDriver: false
    }).start()
  };





  return (
    <View >
    <View style={{height:400, width:400, backgroundColor:'red', transform:[{scale:fadeAnim}]}} >
    </View>
      <Button onPress={()=>{toRight()}} title='click'/>
    </View>
  )
}

export default App;
